con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hello world!"

// Context variable is used to access and modify the properties of your testcase.
//Scope of context lies in testcase only.


log.info context.expand('${#TestCase#age}')
log.info context.expand('${#TestCase#name}')

// to acess/modify any property you have to come through its parent only
// project->test suite-> test cases -> test step
log.info testRunner.testCase.testSuite.testCases["GetDetails"].getPropertyValue("id")

log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")
log.info testRunner.testCase.getPropertyValue("dept")
log.info testRunner.testCase.testSuite.testCases["GetDetails"].testSteps["get"].getPropertyValue("Request")

// set
testRunner.testCase.testSuite.testCases["GetDetails"].setPropertyValue("id", "500")
//
def project =  testRunner.testCase.testSuite.project
project.getPropertyValue("gender")

// Hit addEmployee Request with properties from testcase level

def addReq =  testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"].getPropertyValue("Request")
def name =  testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("name")
def id =  testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")
def age =  testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")
def dept =   testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("dept")


def xmlAdd = new XmlHolder(addReq)

xmlAdd.setNodeValue ("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue ("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue ("//typ:addEmployee/typ:age",age)
xmlAdd.setNodeValue ("//typ:addEmployee/typ:Department",dept)

def newAddXml = xmlAdd.getXml()

log.info newAddXml

testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"].setPropertyValue("Request",newAddXml)

//call the service
// context of AddEmployee testcase sholud be passed as add step which we are trying to run belongs to that testcase 
//addTestStep.run(testRunner, context)

def addTestStep = testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["add"]
def addEmployeeContext = new WsdlTestRunContext(addTestStep)
addTestStep.run(testRunner, addEmployeeContext)


// hit getEmployee and generate response. Validate the response if it has the name which we added in ealier

def getReq =  testRunner.testCase.testSuite.testCases["GetDetails"].testSteps["get"].getPropertyValue("Request")
// def name =  testRunner.testCase.testSuite.testCases["GetDetails"].getPropertyValue("name") - już użyte wcze

def xmlGet = new XmlHolder(getReq)

xmlGet.setNodeValue ("//typ:getEmployeeDetails/typ:employeeName",name)
